import { AppBar, IconButton, Toolbar, Drawer, Button, Avatar, useMediaQuery } from '@mui/material';
import { Menu, AccountCircle, Brightness4, Brightness7 } from '@mui/icons-material';
import { Link } from 'react-router-dom';
import { useState } from 'react';
import useStyles from './styles';
import { Search, Sidebar } from '..';
import { ColorModeContext } from '../../utils/ToggleColorMode';
import { useContext } from 'react';
// To Know whether we are in light or dark mode 
import { useTheme } from '@mui/material/styles';

const NavBar = () => {
    const [mobileOpen, setMobileOpen] = useState(false);
    const classes = useStyles();
    const isMobile = useMediaQuery('(max-width:600px)');
    const theme = useTheme();
    const isAuthenticated = true;
    const colorMode = useContext(ColorModeContext)

    // <AppBar /> is a higher-level component that represents the entire app bar, including its background and title/logo, while <Toolbar /> is a lower-level component used as a child of <AppBar /> to structure the content within the app bar. Together, they allow you to create flexible and customizable app bars for your Material-UI-based application.

    return (
        <>
            <AppBar position='fixed'>
                <Toolbar className={classes.toolbar} >
                    {
                        isMobile && (
                            <IconButton

                                color='inherit'
                                edge='start'

                                onClick={() => setMobileOpen((prev) => !prev)}
                                className={classes.menuButton}
                            >
                                <Menu />
                            </IconButton>)}

                    {/* This is a conditional rendering statement based on the current mode of the theme.
It checks theme.palette.mode to determine whether the current theme mode is 'dark' or not.
If the mode is 'dark', it renders a Brightness7 component, which is typically an icon representing switching to light mode.
If the mode is not 'dark', it renders a Brightness4 component, which is typically an icon representing switching to dark mode. */}

                    <IconButton color="inherit" sx={{ ml: 1 }} onClick={colorMode.toggleColorMode}>
                        {theme.palette.mode === 'dark' ? <Brightness7 /> : <Brightness4 />}
                    </IconButton>
                    {!isMobile && <Search />}

                    <div>
                        {!isAuthenticated ? (
                            <Button color='inherit' onClick={() => { }}>
                                Login &nbsp;<AccountCircle />
                            </Button>
                        ) : (
                            <Button color='inherit' component={Link}
                                to={`/profile/:id`}
                                className={classes.linkButton}
                                onClick={() => { }}
                            >
                                {!isMobile && <>My Movies &nbsp;</>}
                                <Avatar style={{ width: 20, height: 20 }}
                                    alt='Profile'
                                    src='data:image/png;base64,' />

                            </Button>
                        )}
                    </div>


                </Toolbar>
            </AppBar>'
            <div>
                <nav className={classes.drawer}>

                    {isMobile ? (
                        <Drawer

                            varient='temporary'
                            anchor='left'
                            open={mobileOpen}
                            onClose={() => setMobileOpen((prev) => !prev)}
                            className={classes.drawerBackground}
                            classes={{ paper: classes.drawerPaper }}
                            ModalProps={{ keepMounted: true }}
                        >

                            <Sidebar setMobileOpen={setMobileOpen} />;
                        </Drawer>


                    ) : (
                        <Drawer classes={{ paper: classes.drawerPaper }} variant='permanent' transitionDuration={2000} open={true}>
                            <Sidebar setMobileOpen={setMobileOpen} />;

                        </Drawer>


                    )}

                </nav>
            </div>
        </>
    );
};
export default NavBar;